<?phpclass userModel extends MY_Model{    protected $_table = "user";    public function __construct()    {        parent::__construct();    }    public function getAll(){        return $this->query_array();    }    public function getOne($id){        $one = $this->where(" id = '{$id}' ")->query_array();        if(count($one) == 1)            return $one[0];        return false;    }    public function insertUser($data){        return $this->insert($data);    }    public function editUser($data,$id){         return $this->where(" id = {$id} ")->update($data);    }    public function deleteUser($id){        return $this->where(" id = {$id} ")->delete();    }    public function getAdmin($username,$password){        $one = $this                ->where(" username = '{$username}' ")                ->where(" password = md5('{$password}') ")                ->where(" level='1' ")                ->query_array();        if(count($one) == 1)            return $one[0];        return false;    }}